$destPath = "src/assets/work/mamamangos"

foreach ($img in $images) {
    $outputFile = Join-Path $destPath $img.Name
    Write-Host "Downloading $($img.Name)..."
    try {
        Invoke-WebRequest -Uri $img.Url -OutFile $outputFile -UserAgent "Mozilla/5.0"
    } catch {
        Write-Error "Failed to download $($img.Name): $_"
    }
}
